import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';
import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';
import HomePage from './components/HomePage';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve token from local storage
    if (token) {
      setLoggedIn(true);
    }
  }, []);

  const handleLogin = (token) => {
    setLoggedIn(true); // Update loggedIn state
    localStorage.setItem('token', token); // Store token in local storage
  };

  return (
    <Router>
        <Routes>
          <Route path="/register" element={<RegisterForm />} />
          {/* Pass handleLogin function to LoginForm */}
          <Route path="/login" element={<LoginForm setToken={handleLogin} />} />
          {/* Render HomePage when logged in */}
          <Route path="/" element={loggedIn ? <HomePage /> : <Navigate to="/login" />} />
        </Routes>
    </Router>
  );
};

export default App;
