import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';
import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';
import HomePage from './components/HomePage';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const checkLoggedIn = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve token from local storage
        if (token) {
          const response = await axios.get('http://localhost:8000/checkLoggedIn', { params: { token } });
          if (response.data.loggedIn) {
            setLoggedIn(true);
          }
        }
      } catch (error) {
        console.error('Error checking logged in status:', error);
      }
    };

    checkLoggedIn();
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/register" element={<RegisterForm />} />
        {/* Pass setLoggedIn instead of setToken */}
        <Route path="/login" element={<LoginForm setLoggedIn={setLoggedIn} />} />
        <Route path="/" element={loggedIn ? <HomePage /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
